import Foundation

class QuestionBank4
{
    var list = [Question4]()
    
    init()
    {
        list.append(Question4(questionText: "Whose calligraphy adorned the text of the original Constitution of India?", choiceA: "(a) Prem Behari Narain Raizada", choiceB: "(b) Nandlal", choiceC: "(c) Sardar Vallabhbhai Patel", choiceD: "(d) R K Joshi", answer: 1))
        list.append(Question4(questionText: "What was the report prepared by Pandit Motilal Nehru in 1928 called?", choiceA: "(a) Nehru Report ", choiceB: "(b) Swaraj Samvidhaan", choiceC: "(c) Both A and B", choiceD: "(d) None of the above", answer: 3))
        list.append(Question4(questionText: "Under the Prime Ministership of Clement Attlee in Britain, it was decided that the process of making the Indian Constitution and accommodating the demands of the Muslim League would begin immediately, and 3 cabinet ministers were sent to India for this purpose. What did this come to be known as?", choiceA: "(a) Constitution Mission", choiceB: "(b) Free India Mission", choiceC: "(c) Cabinet Mission", choiceD: "(d) Triminsterial Mission", answer: 3))
        list.append(Question4(questionText: "What suggestion(s) were put forth by Muhammad Ali Jinnah at the Simla meeting?", choiceA: "(a) That muslims have their own groups of states that decide their own constitution", choiceB: "(b) The rest of the country (South and Central) could have a separate Constituent Assembly", choiceC: "(c) The native states could decide who they wanted to join or whether to remain independent", choiceD: "(d) All of the above", answer: 4))
        list.append(Question4(questionText: "Maulana Abdul Kalam Azad believed that:", choiceA: "(a) India is one nation and needs its Constitution to be made by one assembly", choiceB: "(b) Muhammad Ali Jinnah’s suggestion intended to create group of provinces on communal lines", choiceC: "(c) Having one Constituent Assembly is a good idea as it will also have representation from the states, regions, religions, and nominated representatives of princely states", choiceD: "(d) All of the above", answer: 4))
        
        list.append(Question4(questionText: "Why did Jinnah ask muslims to take direct action?", choiceA: "(a) He did not agree with the Aims and Objectives Resolution", choiceB: "(b) Despite his suggestions, there was to be only one Constituent assembly to decide India’s future", choiceC: "(c) He felt that there was zero representation of Muslims in the Constituent Assembly", choiceD: "(d) None of the above", answer: 2))
        list.append(Question4(questionText: "When was India’s first Constituent Assembly convened?", choiceA: "(a) December 13, 1947", choiceB: "(b) June 16, 1947", choiceC: "(c) August 15, 1940", choiceD: "(d) December 9, 1946", answer: 4))
         list.append(Question4(questionText: "What advice/talisman did Mahatma Gandhi give to Babu Jagjivan Ram?", choiceA: "(a) To always follow the path of truth and righteousness", choiceB: "(b) To involve the poorest and the weakest in the decision-making process", choiceC: "(c) Whenever in doubt, recall the faces of the poorest and the weakest and contemplate whether a decision will lead to Swaraj for the hungry and starving millions", choiceD: "(d) Whenever in doubt, make sure that the path you take will lead to better economic growth and progress of the nation", answer: 3))
         list.append(Question4(questionText: "Who was selected as the permanent President of the Constituent Assembly?", choiceA: "(a) Dr. Rajendra Prasad", choiceB: "(b) B.R. Ambedkar", choiceC: "(c) Jawaharlal Nehru", choiceD: "(d) Maulana Abdul Kalam Azad", answer: 1))
         list.append(Question4(questionText: "The Aims and Objectives Resolution, presented by Jawaharlal Nehru on 13th December 1946, formed the foundation of the Indian Constitution. What did the proposal cover?", choiceA: "(a) Independence, Unity, and Sovereignty of India", choiceB: "(b) Equality and fundamental rights of the citizens of new India", choiceC: "(c) A description of aims and outline of plans for an Independent India", choiceD: "(d) All of the above", answer: 4))
        
    }
}


